          overflow: hidden;
          background-color: transparent;
      }

    /* ==== ANIMATION === */

      #bc_subhead1 {
          animation-name: animate_subhead_1;
          animation-duration: 9s;
          animation-timing-function: ease-in;
          animation-iteration-count: 1;
      }
      #bc_subhead2 {
          animation-name: animate_subhead_2;
          animation-duration: 9s;
          animation-timing-function: ease-in;
          animation-iteration-count: 1;
      }
      @keyframes animate_subhead_1 {
          0% { opacity: 0; }
          15% { opacity: 0; }
          25% { opacity: 1; }
      }
      @keyframes animate_subhead_2 {
          0% { opacity: 0; }
          70% { left: -30px; opacity: 0; }
          90% { left: 0; opacity: 1;}
      }

      #bc_string_path_1 {
          -webkit-fill: none;
          fill: none;
          -webkit-stroke: white;
          stroke: white;
          -webkit-stroke-width: 10;
          stroke-width: 10;
          -webkit-stroke-dasharray: 237.47;
          stroke-dasharray: 237.47;
          -webkit-stroke-dashoffset: 237.47;
          stroke-dashoffset: 237.47;
          -webkit-animation: brush1 8s linear 1;
          animation: brush1 8s linear 1;
          -webkit-animation-fill-mode: forwards;
          animation-fill-mode: forwards;
          -webkit-animation-timing-function: ease-in-out;
          animation-timing-function: ease-in-out;
      }
      @-webkit-keyframes brush1 {
            0% { stroke-dashoffset: 237.47; }
          10% { stroke-dashoffset: 474.94; }
          100% { stroke-dashoffset: 474.94; }
      }
      @keyframes brush1 {
            0% { stroke-dashoffset: 237.47; }
          10% { stroke-dashoffset: 474.94; }
          100% { stroke-dashoffset: 474.94; }
      }
      #bc_string_path_2 {
          fill: none;
          stroke: white;
          stroke-width: 10;
          stroke-dasharray: 478.88;
          stroke-dashoffset: 478.88;
          animation: brush2 8s linear 1;
          animation-fill-mode: forwards;
      } 
      @keyframes brush2 {
            0% { stroke-dashoffset: 478.88; }
            10% { stroke-dashoffset: 478.88; }
            30% { stroke-dashoffset: 957.76; }
          100% { stroke-dashoffset: 957.76; }
      }
      #bc_string_path_3 {
          fill: none;
          stroke: white;
          stroke-width: 10;
          stroke-dasharray: 132.38;
          stroke-dashoffset: 132.38;
          animation: brush3 8s linear 1;
          animation-fill-mode: forwards;
      }
      @keyframes brush3 {
            0% { stroke-dashoffset: 132.38; }
            30% { stroke-dashoffset: 132.38; }
            35% { stroke-dashoffset: 0; }
          100% { stroke-dashoffset: 0; }
      }
      #bc_string_path_4 {
          fill: none;
          stroke: white;
          stroke-width: 10;
          stroke-dasharray: 275.5;
          stroke-dashoffset: 275.5;
          animation: brush4 8s linear 1;
          animation-fill-mode: forwards;
          animation-timing-function: ease-in-out;
      }    
      @keyframes brush4 {
            0% { stroke-dashoffset: 275.5; }
            35% { stroke-dashoffset: 275.5; }
            60% { stroke-dashoffset: 0; }
          100% { stroke-dashoffset: 0; }
      }
      #bc_string_path_5 {
          fill: none;
          stroke: white;
          stroke-width: 10;
          stroke-dasharray: 277.62;
          stroke-dashoffset: 277.62;
          animation: brush5 8s linear 1;
          animation-fill-mode: forwards;
      } 
      @keyframes brush5 {
            0% { stroke-dashoffset: 277.62; }
            60% { stroke-dashoffset: 277.62; }
            70% { stroke-dashoffset: 555.24; }
          100% { stroke-dashoffset: 555.24; }
      }
      #bc_string_path_6 {
          fill: none;
          stroke: white;
          stroke-width: 10;
          stroke-dasharray: 82.3;
          stroke-dashoffset: 82.3;
          animation: brush6 8s linear 1;
          animation-fill-mode: forwards;
      }
      @keyframes brush6 {
            0% { stroke-dashoffset: 82.3; }
            70% { stroke-dashoffset: 82.3; }  
            80% { stroke-dashoffset: 164.6; }
          100% { stroke-dashoffset: 164.6; }
      }
      #bc_string_path_7 {
          fill: none;
          stroke: white;
          stroke-width: 10;
          stroke-dasharray: 268;
